import {BasicPicker} from "react-color-tools"

const Contenedor = (props) => (
    <div style={{
        background:props.background,
        textAlign:"center",
        border:"1px solid",
        margin:"0 auto",
        padding:"20px",
        width:"700px"
    }}>
        {props.children}
    </div>
)

function Figura1() {
    return (
        <img src="./static/logo-login.png" alt="" height="200" width="200"/>
    );
}

function Seccion1() {
    return (
        <section>
            <h2>Ésta es la sección 1</h2>
            <Figura1/>
        </section>
    );
}

class Index extends React.Component {
    state = {
        background:"hotpink"
    }
    render() {
        return (
            <Contenedor background={this.state.background}>
                <h1>Bienvenidos a mi aplicación React JS</h1>
                <p>Ésta es mi primera app con React JS</p>
                <Seccion1/>
            </Contenedor>
        );
    }
}

export default Index;
